                comment_wrapper: '.js-comment-wrapper',
                form: '.js-comment-form',
                // Блок с превью текста
                preview: '.js-comment-preview',
                // Кнопка свернуть/развернуть все
                fold_all_toggle: '.js-comments-fold-all-toggle',
                // Заголовок
                title: '.js-comments-title',
