            if (this.elements.blogs.length) {
                var orders = this.elements.blogs.getSelectionOrder();
                if (!orders || !orders.length) {
                    orders = this.elements.blogs.val();
                }
                if (orders && orders.length) {
                    this.element.lsContent('option', 'params.topic[blogs_id_raw]', orders);
                }
            }
